(3, 'School Timings', 'Please note that the school timings remain unchanged. Kindly ensure that your child arrives at school on time, ready to engage in a day of learning and growth.', '2024-11-13', '2024-11-13', '[1]', 1, 1, NULL, NULL, 1, 1, 1, 1),
(4, 'Uniform and Dress Code', 'We appreciate your cooperation in ensuring that students adhere to the prescribed uniform and dress code. This contributes to a positive and disciplined learning environment.', '2024-11-13', '2024-11-13', '[1]', 1, 1, NULL, NULL, 1, 1, 1, 1),
(5, 'Safety and Security', 'The safety and security of our students are of utmost importance. We request parents to follow the designated drop-off and pick-up points, and kindly inform the school in advance of any changes in transportation arrangements.', '2024-11-13', '2024-11-13', '[1]', 1, 1, NULL, NULL, 1, 1, 1, 1),
(6, 'Winter Break', 'InfixEdu School will be closed for the winter break from 15/12/2024 to 31/12/2024. We wish all our students and their families a joyful and restful holiday season.', '2024-11-13', '2024-11-13', '[1]', 1, 1, NULL, NULL, 1, 1, 1, 1),
(7, 'Feedback and Suggestions', 'We value your feedback and suggestions. If you have any ideas or concerns you would like to share, please feel free to reach out to the school administration.', '2024-11-13', '2024-11-13', '[1]', 1, 1, NULL, NULL, 1, 1, 1, 1);

